pragma solidity >= 0.4.22;

import "./../libraries/0x/contracts/erc20/contracts/src/MintableERC20Token.sol";
import "./MarketOutcomeToken.sol";
/* 
	For this implementation of MultiMarketIndexToken we're assuming that all markets are conmnected
	and that the user will choose to take one particulair position in these markets. 
*/


// Make mintable unlimited allowance token.
contract MultiMarketIndexToken is MintableERC20Token {
	string public name = "MultiMarketIndexToken";
	string public symbol = "MMIT";
	uint256 public decimals = 18;

	bool marketsResolved = false;
	address[] public index;
	mapping(address => uint256) indexToWeight;

	constructor(address[] _index, uint256[] _weights) public {
		require(_weights.length == _index.length);
		for (uint i = 0; i < _index.length; i++) {
			indexToWeight[_index[i]] = _weights[i];
		}
		index = _index;
		// Set unlimited allowance for the 0x contract
	}

	/// @dev mints new tokens
	/// @param _outcome is the position you're taking in the index.
	function mint(uint _outcome) public payable {
		require(msg.value > 0);
		purchaseIndex(msg.value);
	}

	/// @dev purchases the index positions
	/// @param _amount is the amount of 
	function purchaseIndex(uint _amount) internal {
		for (uint i = 0; i < index.length; i++) {
			uint256 weightedAmount = (indexToWeight[index[i]] * _amount ) / 100;
			MarketOutcomeToken(index[i]).purchase.value(weightedAmount)();
		}
	}

	function withdraw() public {}

	function payout() public {}

	function indexResolved() public view {}



	/* 
		Specs: 
			- mint() 
				- mint formula is: msg.value * 100 -> for every ETH spend receive 100 MultiMarketIndexTokens
				- function will need to have a msg.value > 0 
				- function will call addToIndex() which will distribute the value accross an array of predefined augur markets
			- purchaseIndex()
				- Distributes the provided eth over predefined Augur markets that together form a fund
			- indexResolved()
			 	- Loops through the predefined Augur markets and returns wheter they are resovled
			- withdraw()
			- payout()

	*/
	// TODO: Everything

}
